  hoveredCategory: any = null;

  selectedCategory: any = null;
  selectedProduct: any = null;

  showCategories: boolean = true;
  quantity: number = 1;  // New property for quantity

  toggleCategories() {
    this.showCategories = !this.showCategories;
    this.selectedCategory = null;
    this.selectedProduct = null;
  }

  onCategoryClick(category: any) {
    this.selectedCategory = category;
    this.selectedProduct = null;
  }

  showProductDetails(product: any) {
    this.selectedProduct = product;
    this.selectedCategory = null;
    this.showCategories = false;
  }

  addToCart(product: any) {
    alert(`${product.name} has been added to your cart!`);
  }

  closeProductDetails() {
    this.selectedProduct = null;
    this.showCategories = true;
  }

  orderProduct(product: any) {
    const totalPrice = product.price * this.quantity;
    alert(`You have ordered ${this.quantity} ${product.name}(s) for ₹${totalPrice}`);
  }
  goBack() {
    if (this.selectedProduct) {
      // Go back to the products list
      this.selectedProduct = null;
      this.showCategories = false;
    } else if (this.selectedCategory) {
      // Go back to the categories list
      this.selectedCategory = null;
      this.showCategories = true;
    }
    this.hoveredCategory = null; // Reset hovered category
    this.quantity = 1; // Reset quantity to 1
  }
  
  
  toggleWishlist(product: any) {
    product.liked = !product.liked; // Toggle liked state
    if (product.liked) {
      alert(`${product.name} has been added to your wishlist!`);
    } else {
      alert(`${product.name} has been removed from your wishlist!`);
    }
  }

  onCategoryHover(category: any) {
    this.hoveredCategory = category;
  }

  onCategoryLeave() {
    this.hoveredCategory = null;
  }
